/**
 * Official Type definitions for LemonadeJS plugins
 * https://lemonadejs.net
 * Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
 */

declare function Color(el: HTMLElement, options?: Color.Options): Color.Instance;

declare namespace Color {
    interface Options {
        /** Input */
        input?: HTMLInputElement | 'auto';
        /** Available colors */
        palette?: string[];
        /** Current value */
        value?: string;
        /** Text to display on input without value */
        placeholder?: string;
        /** Initial state */
        closed?: boolean;
        /** Picker Type */
        type?: 'inline' | 'input' | undefined;
        /** Close on change */
        closeOnChange?: boolean;
        /** On open event */
        onopen?: () => void;
        /** On close event */
        onclose?: () => void;
        /** On update event */
        onchange?: (self: object, value: string) => void;
    }

    interface Instance {
        /** Available colors */
        palette?: string[];
        /** Current value */
        value?: string;
        /** Text to display on input without value */
        placeholder?: string;
        /** Picker state */
        closed: boolean;
        /** Set a new value */
        setValue: (v: string) => void;
        /** Get the current value */
        getValue: () => string;
    }
}

export default Color;