/**
 * Official Type definitions for LemonadeJS plugins
 * https://lemonadejs.net
 */

declare function Rating(el: HTMLElement, options?: Rating.Options): Rating.Instance;

declare namespace Rating {

    interface Options {
        /** Number of stars */
        number?: number;
        /** Form element name */
        name?: string;
        /** Current value */
        value?: number;
        /** Tooltips displayed on hover */
        tooltip?: string | string[];
        /** Size */
        size?: 'default' | 'small';
        /** Onchange Event */
        onchange?: (instance: Instance, newValue: number) => void;
    }

    interface Instance {
        /** Number of stars */
        number: number;
        /** Form element name */
        name?: string;
        /** Current value */
        value: number;
        /** Tooltips displayed on hover */
        tooltip?: string | string[];
        /** Size */
        size?: 'default' | 'small';
        /** Gets the current value */
        getValue: () => number;
        /** Sets the value */
        setValue: (index: number) => void;
    }
}

export default Rating;